mapper.insert:
insert 
  into community
  (
    commu_no
  , commu_cate
  , commu_title
  , commu_writer
  , commu_content
  , commu_origin
  , commu_change
  )
  values
  (
    seq_comno.nextval
  , #{commuCate}
  , #{commuTitle}
  , #{commuWriter}
  , #{commuContent}
  , #{commuOrigin}
  , #{commuChange}
  )

mapper.update:
update
       community
   set commu_title = #{commuTitle}
     , commu_content = #{commuContent}
     , commu_origin = #{commuOrigin}
     , commu_change = #{commuChange}
 where commu_no = #{commuNo}

mapper.insert:
insert
  into commu_reply
  (
    com_re_no
  , com_re_content
  , ref_cno
  , com_re_writer
  , member_no
  )
  values
  (
    SEQ_COMRNO.nextval
  , #{comReContent}
  , #{refCommuNo}
  , #{comReWriter}
  , #{memberNo}
  )
