mapper.insert:
insert
  into faq
  (
    faq_no
  , faq_cat
  , faq_title
  , faq_content
  )
  values
  (
    seq_faqno.nextval
  , #{faqCat}
  , #{faqTitle}
  , #{faqContent}
  )

mapper.update:
update
       faq
   set faq_cat = #{faqCat}
     , faq_title = #{faqTitle}
     , faq_content = #{faqContent}
 where faq_no = #{faqNo}
