mapper.select:
select * from ( select p.project_no , p.project_thum , p.project_title , c.cat_no , c.cat_name , m.partner_name , sum(o.total_price) total_price , floor(sum(o.total_price) / p.project_gprice * 100) percentage , p.project_gprice , p.project_start_dt , p.project_end_dt , ceil(project_end_dt - sysdate) dday from tb_order o , project p , category c , member m where p.project_no = o.project_no(+) and p.cat_no = c.cat_no and p.member_no = m.member_no and p.project_status = 3 and ceil(project_end_dt - sysdate) > 0 and ceil(sysdate - project_start_dt) < 5
group by p.project_no , p.project_title , p.project_thum , c.cat_no , c.cat_name , m.partner_name , p.project_gprice , p.project_start_dt , p.project_end_dt
)

mapper.insert:
insert
  into tb_order
     (
       order_no
     , project_no
     , member_no
     , reward_no
     , pur_email
     , order_plus
     , total_price
     , bank_name
     , bank_account
     , receiver_name
     , receiver_phone
     , address_no
     , address_detail
     , address
     , shipping_req
     , order_date
     , order_count
     , order_option
     )
values
     (
       seq_odrno.nextval
     , #{projectNo}
     , #{memberNo}
     , #{rewardNo}
     , #{purEmail}
     , #{orderPlus}
     , #{totalPrice}
     , #{bankName}
     , #{bankAccount}
     , #{receiverName}
     , #{receiverPhone}
     , #{addressNo}
     , #{addressDetail}
     , #{address}
     , #{shippingReq}
     , sysdate
     , #{orderCount}
     , #{orderOption}
     )

mapper.update:
    	update
		       reward
		   set reward_stock = reward_stock-#{orderCount}
		 where reward_no = #{rewardNo}
		   and reward_status = 'Y'

