mapper.insert:
insert
  into member
  (
  	member_no
  , member_name
  ,	member_id
  , member_pwd
  )
  values
  (
  	seq_mno.nextval
  , #{memberName}
  , #{memberId}
  , #{memberPwd}
  )

mapper.update:
		update
  				member
  			set member_id = #{memberId}
   			  , member_name = #{memberName}
  			  , member_create_dt = sysdate
  		  where member_id = #{memberId}

mapper.insert:
insert
  into member
  (
  	member_id
  , member_pwd
  )
  values
  (
    #{memberId}
  , #{memberPwd}
  )
