mapper.update:
update
        member
   set  member_phone = #{memberPhone}
      , member_profile = #{memberProfile}
      , partner_name = #{partnerName}
      , partner_intro =#{partnerIntro}
 where member_id = #{memberId}

update
        member
   set  member_pwd = #{memberPwd}
  where member_no = #{memberNo}

mapper.delete:
delete 
 	from follow
   where member_no = #{memberNo}
     and follow_member_no = #{followMemberNo}

mapper.insert:
insert
  into follow
  (
    member_no
  , follow_member_no
  )
  values
  (
    #{memberNo}
  , #{followMemberNo}

  )

mapper.select:
select 
       count(*)
  from follow
 where member_no = #{followMemberNo}
   and follow_member_no = #{memberNo}

mapper.update:
update
        tb_order
   set  receiver_name = #{receiverName}
      , receiver_phone = #{receiverPhone}
      , address_no = #{addressNo}
      , address = #{address}
      , address_detail = #{addressDetail}
 where order_no = #{orderNo}

 update
         tb_order
   set   order_status = '2'
       , bank_name = #{bankName}
       , bank_account = #{bankAccount}
where order_no = #{orderNo}

 update
         member
   set   partner_name = #{partnerName}
       , partner_intro = #{partnerIntro}
       , member_profile = #{memberProfile}
       , partner_join = 'Y'
where member_no = #{memberNo}
